Run # First check if the exact simulator exists
  # First check if the exact simulator exists
  if xcrun simctl list devices available | grep -q "iPhone 14 Pro.*17.4"; then
    echo "Testing on iPhone 14 Pro with iOS 17.4"
    DESTINATION="platform=iOS Simulator,name=iPhone 14 Pro,OS=17.4"
  else
    echo "Exact simulator not found, using generic destination"
    DESTINATION="platform=iOS Simulator,name=iPhone 14 Pro"
  fi

  # Remove the damaged xcodeproj
  rm -rf StoicExecutive.xcodeproj

  # Create workspace by opening the package
  xcodebuild -list -json

  # Use xcodebuild with automatic scheme detection
  xcodebuild clean build test \
    -scheme StoicExecutive \
    -destination "$DESTINATION" \
    CODE_SIGN_IDENTITY="" \
    CODE_SIGNING_REQUIRED=NO \
    -resultBundlePath "TestResults-iphone-14-pro" \
    -derivedDataPath DerivedData \
    -disableAutomaticPackageResolution

  shell: /bin/bash -e {0}
